
import React , {useState , useEffect } from "react";
import { Link , Navigate , useLocation  } from "react-router-dom";
import {AiOutlineSearch} from "react-icons/ai"
import "./index.css"
import NewPost from "../newPost";

const Nav = () => {

const [activePath, setActivePath] = useState("")
const [newPost, setNewPost] = useState(false)

const { pathname } = useLocation()

useEffect(() => {
    setActivePath(pathname)
    setNewPost(false)
}, [pathname])


    return (
      <div>
        <nav className="navbar is-fixed-top" id="nav">
            <div id="leftNav">
            <Link to="/" className="mr-2">
                <img src="data:image/png;base64," 
                className={activePath === "/" ? "navItemActive" : null }/>
            </Link> 
            {activePath === "/" ? (<div className="field has-addons">
  <div className="control is-expanded">
    
    <input className="input is-rounded" type="text" id="searchBar" placeholder="Search..."/>
  </div>
  <div className="control">
    <button className="button is-info is-rounded">
      <AiOutlineSearch/>
    </button>
  </div>
</div>)  : null}

           </div>
           <div id='rightNav'>
        {!newPost ? (
          <button className='button is-rounded is-info mr-2' id='newPostButton' onClick={() => setNewPost(true)}>
             Create Post
          </button>
        ) : (
          null
        )}
      
            
            <Link to="/myprofile">
               <img src="https://www.pngkey.com/png/full/73-730477_first-name-profile-image-placeholder-png.png"  className={activePath === "/myprofile" ? "navItemActive" : null }  />
            </Link>
            </div>
          

      </nav>

      
      {newPost &&  <NewPost setFalse={setNewPost}/>}
      </div>
    );
};

export default Nav;






